<?php/** * This file is part of secra/CachingProxy. * * (c) Sebastian Krüger <krueger@secra.de> * * For the full copyright and license information, please view the LICENSE * file that was distributed with this source code. */namespace secra\CachingProxy\Tests;use org\bovigo\vfs\vfsStream;use secra\CachingProxy\AbstractCachingProxyTestClass;// include Testclassrequire_once 'AbstractCachingProxyTestClass.php';/** * CachingProxyTest * * @category test * @package de.secra.cachingproxy * @author Sebastian Krüger <krueger@secra.de> * @copyright 2014 Sebastian Krüger * @license http://www.opensource.org/licenses/MIT The MIT License */class AbstractCachingProxyTest extends \PHPUnit_Framework_TestCase{    /*     * @type AbstractCachingProxy    */    private $cachingproxystub;    /*     * @type vfsStreamDirectory    */    protected $vfsroot;    public function setUp()    {        // Set vfs Testfilesystem        $this->vfsroot = vfsStream::setup('testroot');        // build up structure with testfiles        $vfsstructure = array(            'testfiles' => array(                'testfile1.test' => 'function testfunction() { var d1 = document.getElementById( "hitme" ); }',                'testfile2.test' => 'function testfuncion2(demo) { colorDiv( var d2 = document.getElementById( "bluebox" ); }',                'testfile1.min.test' => 'function testfunction(){var d1=document.getElementById("hitme");}',            ),            'cache' => array()        );        // fill structure below the vfs testdir        vfsStream::create($vfsstructure, $this->vfsroot);        $testStreamUrl=vfsStream::url('testroot');        // instanciate teststub class for abstract class        $this->cachingproxystub = new AbstractCachingProxyTestClass($testStreamUrl."/","cache");    }    // TODO: Check how function addFile and getIncludeFileset can test without inferences    /**     * @test     * @covers secra\CachingProxy\AbstractCachingProxy::addFile()     */    public function testAddFile()    {        // TODO: Test intern files        // Add Files with different protocol definations        $this->cachingproxystub->addFile("https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&sensor=FALSE");        $this->cachingproxystub->addFile("http://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&sensor=FALSE");        $this->cachingproxystub->addFile("//maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&sensor=FALSE");        // retrun all extern files        $fileset = $this->cachingproxystub->getIncludeFileset();        // count the list of files        $this->assertCount(3, $fileset);    }    /**     * @test     * @covers secra\Cachingproxy\AbstractCachingProxy::addFile()     * @covers secra\Cachingproxy\AbstractCachingProxy::getIncludeFileset()     * @covers secra\CachingProxy\AbstractCachingProxy::getCacheFile()     */    public function testGetIncludeFilesets()    {        // TODO: Test Intern files        // Add nothing and get emty list        $fileset = $this->cachingproxystub->getIncludeFileset();        $this->assertCount(0, $fileset);        // Add Files with different protocol definations        $urllist = array();        $urllist[] = "https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY0&sensor=FALSE";        $urllist[] = "http://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY1&sensor=FALSE";        $urllist[] = "//maps.googleapis.com/maps/api/js?key=YOUR_API_KEY2&sensor=FALSE";        foreach($urllist as $url) {            $this->cachingproxystub->addFile($url);        }        $fileset = $this->cachingproxystub->getIncludeFileset();        // See if count fitts first        $this->assertCount(3, $fileset);        // See if we get them back by url string        foreach($urllist as $url) {            $this->assertContains($url, $fileset);        }    }    /**     * @test     *     * @covers secra\Cachingproxy\AbstractCachingProxy::enableDebugmode()     * @covers secra\Cachingproxy\AbstractCachingProxy::disableDebugmode()     *     */    public function testDebugmode()    {        // debugmode is disabled as default        $this->assertFalse($this->cachingproxystub->getDebugMode());        // enable it and test return value        $this->assertNull($this->cachingproxystub->enableDebugmode());        // test if mode is set        $this->assertTrue($this->cachingproxystub->getDebugMode());        // unset and test once more        $this->assertNull($this->cachingproxystub->disableDebugmode());        $this->assertFalse($this->cachingproxystub->getDebugMode());    }    /**     * @test     *     * @covers secra\Cachingproxy\AbstractCachingProxy::setWebserverRootPath()     *     */    public function testSetWebserverRootPath()    {        // Build reflection of protected function        $method = new \ReflectionMethod('\secra\CachingProxy\AbstractCachingProxyTestClass', 'setWebserverRootPath');        $method->setAccessible(true);        // Set nonexisting rootpath        $testStreamUrl=vfsStream::url('testroot');        $this->assertFalse($method->invoke($this->cachingproxystub, $testStreamUrl."/nonexitstingpath"));        $this->assertNull($this->cachingproxystub->getDocrootpath());        // Set existing Rootpath        $testStreamUrl=vfsStream::url('testroot');        $this->assertTrue($method->invoke($this->cachingproxystub, $testStreamUrl));        $this->assertEquals("vfs://testroot/", $this->cachingproxystub->getDocrootpath());        $this->assertTrue($method->invoke($this->cachingproxystub, $testStreamUrl."/"));        $this->assertEquals("vfs://testroot/", $this->cachingproxystub->getDocrootpath());    }    /**     * @test     *     * @covers secra\Cachingproxy\AbstractCachingProxy::setCachepath()     *     */    public function testSetCachepath()    {        // Build reflection of protected function        $method = new \ReflectionMethod('\secra\CachingProxy\AbstractCachingProxyTestClass', 'setCachepath');        $method->setAccessible(true);        // Set nonexisting cachepath        $this->assertFalse($method->invoke($this->cachingproxystub, "/nonexitstingpath"));        // Set existing cachepath        $this->assertTrue($method->invoke($this->cachingproxystub, "cache"));        // the double trailing slash is a tribute to mock the realpath function        $this->assertEquals("vfs://testroot/cache//", $this->cachingproxystub->getCachepath());        $this->assertEquals("/cache/", $this->cachingproxystub->getRelCachepath());        $this->assertTrue($method->invoke($this->cachingproxystub, "cache/"));        $this->assertEquals("vfs://testroot/cache//", $this->cachingproxystub->getCachepath());        $this->assertEquals("/cache/", $this->cachingproxystub->getRelCachepath());    }    /**     * @test     * @covers secra\Cachingproxy\AbstractCachingProxy::makeAbsolutPath()     */ /*    Comment because override the realpath function    public function testMakeAbsolutPath()    {        // Build reflection of private function        $method = new \ReflectionMethod('\secra\CachingProxy\AbstractCachingProxyTestClass', 'makeAbsolutPath');        $method->setAccessible(true);        // You can't test this function via vfs file system : https://github.com/mikey179/vfsStream/wiki/Known-Issues        // and with real test file system you have fragile test because of different file systems        // but is is worth a test, if function return false on not existing path        $this->assertFalse($method->invoke(new AbstractCachingProxyTestClass("/","/cache"), "/this/path/wont/exists"));    } */    /**     * @test     * @covers secra\Cachingproxy\AbstractCachingProxy::makeMinifiPath()     */    public function testMakeMinifiPath()    {        // Build reflection of private Function        $method = new \ReflectionMethod('\secra\CachingProxy\AbstractCachingProxyTestClass', 'makeMinifiPath');        $method->setAccessible(true);        $testpath = "/var/www/htdocs/css/file.css";        $this->assertEquals("/var/www/htdocs/css/file.min.css", $method->invoke(new AbstractCachingProxyTestClass('/','cache'), $testpath));        $testpath = "/var/www/htdocs/css/file.js";        $this->assertEquals("/var/www/htdocs/css/file.min.js", $method->invoke(new AbstractCachingProxyTestClass('/','cache'), $testpath));        $testpath = "/var/www/htdocs/css/here.is.a.js";        $this->assertEquals("/var/www/htdocs/css/here.is.a.min.js", $method->invoke(new AbstractCachingProxyTestClass('/','cache'), $testpath));        $testpath = "/var/www/htdocs/css/file..js";        $this->assertEquals("/var/www/htdocs/css/file..min.js", $method->invoke(new AbstractCachingProxyTestClass('/','cache'), $testpath));    }}